    const formatArray = (arr: any[]): any[] => {
        return arr.map((v) => {
            let name = v.name.toLowerCase()
            name = name.startsWith('pa_') ? name.replace('pa_', '') : name
            const value = v.value.toLowerCase()
            return { name, value }
        })
    }

